import { Pool } from "pg";
import { drizzle } from "drizzle-orm/node-postgres";
import * as schema from "@shared/schema";
import "dotenv/config";

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: { rejectUnauthorized: false }, // Render requires SSL
});
export const db = drizzle(pool, { schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertTicketSchema, updateTicketStatusSchema, loginSchema, insertCustomerSchema } from "@shared/schema";
import jwt from "jsonwebtoken";
import bcrypt from "bcrypt";

interface AuthenticatedRequest extends Request {
  user: {
    userId: string;
    username: string;
    role: string;
  };
}

const JWT_SECRET = process.env.JWT_SECRET || "satyasri-computers-secret-key";

// Middleware to verify JWT token
function authenticateToken(req: any, res: any, next: any) {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.status(401).json({ message: 'Access token required' });
  }

  jwt.verify(token, JWT_SECRET, (err: any, user: any) => {
    if (err) {
      return res.status(403).json({ message: 'Invalid or expired token' });
    }
    req.user = user;
    next();
  });
}

// Middleware to check role
function requireRole(roles: string[]) {
  return (req: any, res: any, next: any) => {
    if (!req.user || !roles.includes(req.user.role)) {
      return res.status(403).json({ message: 'Insufficient permissions' });
    }
    next();
  };
}

function generateTicketId(date: Date, sequence: number): string {
  const dateStr = date.toISOString().slice(0, 10).replace(/-/g, '');
  const sequenceStr = String(sequence).padStart(4, '0');
  return `SATY-${dateStr}-${sequenceStr}`;
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication routes
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { username, password, role } = loginSchema.parse(req.body);

      const user = await storage.getUserByUsername(username);
      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // For demo purposes, we'll do simple password comparison
      // In production, use bcrypt.compare()
      if (user.password !== password || user.role !== role) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const token = jwt.sign(
        { userId: user.id, username: user.username, role: user.role },
        JWT_SECRET,
        { expiresIn: '24h' }
      );

      res.json({
        user: {
          id: user.id,
          username: user.username,
          fullName: user.fullName,
          role: user.role,
          email: user.email,
          phone: user.phone
        },
        token
      });
    } catch (error) {
      res.status(400).json({ message: "Invalid request data" });
    }
  });

  app.post("/api/auth/verify", authenticateToken, async (req, res) => {
    try {
      const user = await storage.getUser(req.user.userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({
        user: {
          id: user.id,
          username: user.username,
          fullName: user.fullName,
          role: user.role,
          email: user.email,
          phone: user.phone
        }
      });
    } catch (error) {
      res.status(500).json({ message: "Server error" });
    }
  });

  // Customer routes
  app.get("/api/customers", authenticateToken, requireRole(['frontdesk']), async (req: AuthenticatedRequest, res) => {
    try {
      const customers = await storage.getAllCustomers();
      res.json(customers);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch customers" });
    }
  });

  app.post("/api/customers", authenticateToken, requireRole(['frontdesk']), async (req, res) => {
    try {
      const customerData = insertCustomerSchema.parse(req.body);
      const customer = await storage.createCustomer(customerData);
      res.status(201).json(customer);
    } catch (error) {
      res.status(400).json({ message: "Invalid customer data" });
    }
  });

  // Ticket routes
  app.get("/api/tickets", authenticateToken, async (req: AuthenticatedRequest, res) => {
    try {
      let tickets;

      if (req.user.role === 'technician') {
        tickets = await storage.getTicketsByTechnician(req.user.userId);
      } else {
        const status = req.query.status as string;
        if (status) {
          tickets = await storage.getTicketsByStatus(status);
        } else {
          tickets = await storage.getAllTickets();
        }
      }

      res.json(tickets);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch tickets" });
    }
  });

  app.get("/api/tickets/:ticketId", authenticateToken, async (req, res) => {
    try {
      const ticket = await storage.getTicketByTicketId(req.params.ticketId);
      if (!ticket) {
        return res.status(404).json({ message: "Ticket not found" });
      }

      const customer = await storage.getCustomer(ticket.customerId);
      let assignedTechnicianName: string | undefined;

      if (ticket.assignedTechnician) {
        const technician = await storage.getUser(ticket.assignedTechnician);
        assignedTechnicianName = technician?.fullName;
      }

      res.json({
        ...ticket,
        customer,
        assignedTechnicianName
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch ticket details" });
    }
  });

  app.post("/api/tickets", authenticateToken, requireRole(['frontdesk']), async (req, res) => {
    try {
      const ticketData = insertTicketSchema.parse(req.body);
      console.log('Received ticket data:', ticketData);

      // Find or create customer
      let customer = await storage.getCustomerByPhone(ticketData.customerPhone);
      console.log('Found customer:', customer);
      if (!customer) {
        customer = await storage.createCustomer({
          name: ticketData.customerName,
          phone: ticketData.customerPhone,
          email: ticketData.customerEmail,
          address: ticketData.customerAddress
        });
      }

      // Generate ticket ID
      const today = new Date();
      console.log('Today:', today);
      const dateStr = today.toISOString().slice(0, 10).replace(/-/g, '');
      console.log('Date string:', dateStr);
      const sequence = await storage.getNextTicketSequence(dateStr);
      console.log('Sequence number:', sequence);
      const ticketId = generateTicketId(today, sequence);
      console.log('Generated ticket ID:', ticketId);

      // Create ticket
      const { customerName, customerPhone, customerEmail, customerAddress, ...ticketFields } = ticketData;
      const ticket = await storage.createTicket({
        ...ticketFields,
        ticketId,
        customerId: customer.id
      } as any);
      console.log('Created ticket:', ticket);

      res.status(201).json({ ...ticket, customer });
    } catch (error) {
      console.error('Error creating ticket:', error);
      res.status(400).json({ message: "Invalid ticket data" });
    }
  });

  app.patch("/api/tickets/:ticketId/status", authenticateToken, async (req: AuthenticatedRequest, res) => {
    try {
      const updateData = updateTicketStatusSchema.parse(req.body);

      const updatedTicket = await storage.updateTicketStatus(
        req.params.ticketId,
        { ...updateData, technicianId: req.user.userId }
      );

      if (!updatedTicket) {
        return res.status(404).json({ message: "Ticket not found" });
      }

      const customer = await storage.getCustomer(updatedTicket.customerId);
      res.json({ ...updatedTicket, customer });
    } catch (error) {
      res.status(400).json({ message: "Invalid update data" });
    }
  });

  // Stats routes
  app.get("/api/stats", authenticateToken, async (req: AuthenticatedRequest, res) => {
    try {
      const allTickets = await storage.getAllTickets();

      const stats = {
        totalTickets: allTickets.length,
        pendingTickets: allTickets.filter(t => t.serviceStatus === 'Pending').length,
        inProgressTickets: allTickets.filter(t => t.serviceStatus === 'In Progress').length,
        completedTickets: allTickets.filter(t => t.serviceStatus === 'Completed').length,
        deliveredTickets: allTickets.filter(t => t.serviceStatus === 'Delivered').length,
        totalCustomers: (await storage.getAllCustomers()).length,
        todayCompleted: allTickets.filter(t => {
          const today = new Date().toISOString().slice(0, 10);
          return t.completedAt && t.completedAt.toISOString().slice(0, 10) === today;
        }).length,
        monthlyRevenue: allTickets
          .filter(t => t.finalCost && t.serviceStatus === 'Delivered')
          .reduce((sum, t) => sum + parseFloat(t.finalCost || '0'), 0)
      };

      // Role-specific stats
      if (req.user.role === 'technician') {
        const myTickets = allTickets.filter(t => t.assignedTechnician === req.user.userId);
        (stats as any).assignedToMe = myTickets.length;
        (stats as any).myInProgress = myTickets.filter(t => t.serviceStatus === 'In Progress').length;
        (stats as any).myCompletedToday = myTickets.filter(t => {
          const today = new Date().toISOString().slice(0, 10);
          return t.completedAt && t.completedAt.toISOString().slice(0, 10) === today;
        }).length;
      }

      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch stats" });
    }
  });

    // Reports routes
  app.get("/api/reports", authenticateToken, requireRole(['frontdesk']), async (req: AuthenticatedRequest, res) => {
    try {
      const allTickets = await storage.getAllTickets();
      const allCustomers = await storage.getAllCustomers();
      // Calculate date ranges
      const now = new Date();
      const startOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
      const startOfWeek = new Date(now.setDate(now.getDate() - now.getDay()));
      const today = new Date().toISOString().slice(0, 10);
      // Ticket status breakdown
      const statusBreakdown = {
        pending: allTickets.filter(t => t.serviceStatus === 'Pending').length,
        inProgress: allTickets.filter(t => t.serviceStatus === 'In Progress').length,
        waitingForParts: allTickets.filter(t => t.serviceStatus === 'Waiting for Parts').length,
        testing: allTickets.filter(t => t.serviceStatus === 'Testing').length,
        completed: allTickets.filter(t => t.serviceStatus === 'Completed').length,
        delivered: allTickets.filter(t => t.serviceStatus === 'Delivered').length,
      };
      // Revenue calculations
      const thisMonthRevenue = allTickets
        .filter(t => t.finalCost && t.serviceStatus === 'Delivered' && 
                new Date(t.completedAt || t.createdAt) >= startOfMonth)
        .reduce((sum, t) => sum + parseFloat(t.finalCost || '0'), 0);
      const thisWeekRevenue = allTickets
        .filter(t => t.finalCost && t.serviceStatus === 'Delivered' && 
                new Date(t.completedAt || t.createdAt) >= startOfWeek)
        .reduce((sum, t) => sum + parseFloat(t.finalCost || '0'), 0);
      const todayRevenue = allTickets
        .filter(t => t.finalCost && t.serviceStatus === 'Delivered' && 
                (t.completedAt?.toISOString().slice(0, 10) === today))
        .reduce((sum, t) => sum + parseFloat(t.finalCost || '0'), 0);
      // Top issues
      const issueCategories = allTickets.reduce((acc: any, ticket) => {
        acc[ticket.issueCategory] = (acc[ticket.issueCategory] || 0) + 1;
        return acc;
      }, {});
      // Performance metrics
      const avgResolutionTime = allTickets
        .filter(t => t.completedAt)
        .reduce((sum, t) => {
          const start = new Date(t.createdAt).getTime();
          const end = new Date(t.completedAt!).getTime();
          return sum + (end - start);
        }, 0) / allTickets.filter(t => t.completedAt).length || 0;
      const reports = {
        summary: {
          totalTickets: allTickets.length,
          totalCustomers: allCustomers.length,
          totalRevenue: allTickets
            .filter(t => t.finalCost && t.serviceStatus === 'Delivered')
            .reduce((sum, t) => sum + parseFloat(t.finalCost || '0'), 0),
          avgResolutionDays: Math.round(avgResolutionTime / (1000 * 60 * 60 * 24))
        },
        revenue: {
          today: todayRevenue,
          thisWeek: thisWeekRevenue,
          thisMonth: thisMonthRevenue
        },
        statusBreakdown,
        topIssues: Object.entries(issueCategories)
          .sort(([,a]: any, [,b]: any) => b - a)
          .slice(0, 5)
          .map(([category, count]) => ({ category, count })),
        recentTickets: allTickets.slice(0, 10)
      };
      res.json(reports);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch reports" });
    }
  });
  // Auth logout route
  app.post("/api/auth/logout", authenticateToken, (req, res) => {
    // With JWT, logout is handled client-side by removing the token
    res.json({ message: "Logged out successfully" });
  });


  // Communication routes
  app.post("/api/communication/send", authenticateToken, requireRole(['frontdesk']), async (req, res) => {
    try {
      const { ticketId, messageType, message } = req.body;

      const ticket = await storage.getTicketByTicketId(ticketId);
      if (!ticket) {
        return res.status(404).json({ message: "Ticket not found" });
      }

      const customer = await storage.getCustomer(ticket.customerId);
      if (!customer) {
        return res.status(404).json({ message: "Customer not found" });
      }

      // TODO: Implement actual SMS/WhatsApp sending
      // For now, just log the message
      console.log(`Sending ${messageType} to ${customer.phone}: ${message}`);

      res.json({ 
        success: true, 
        message: `${messageType.toUpperCase()} sent successfully to ${customer.name}` 
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to send message" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  customers,
  tickets,
  type User,
  type InsertUser,
  type Customer,
  type InsertCustomer,
  type Ticket,
  type InsertTicket,
  type TicketWithCustomer,
  type UpdateTicketStatus,
} from "@shared/schema";
import { randomUUID } from "crypto";
import { db } from "./db";
import { eq, desc, sql } from "drizzle-orm";
import { ticket_sequences } from "@shared/schema";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Customer methods
  getCustomer(id: string): Promise<Customer | undefined>;
  getCustomerByPhone(phone: string): Promise<Customer | undefined>;
  createCustomer(customer: InsertCustomer): Promise<Customer>;
  getAllCustomers(): Promise<Customer[]>;

  // Ticket methods
  getTicket(id: string): Promise<Ticket | undefined>;
  getTicketByTicketId(ticketId: string): Promise<Ticket | undefined>;
  createTicket(
    ticket: InsertTicket & { ticketId: string; customerId: string }
  ): Promise<Ticket>;
  updateTicketStatus(
    ticketId: string,
    update: UpdateTicketStatus & { technicianId?: string }
  ): Promise<Ticket | undefined>;
  getAllTickets(): Promise<TicketWithCustomer[]>;
  getTicketsByTechnician(technicianId: string): Promise<TicketWithCustomer[]>;
  getTicketsByStatus(status: string): Promise<TicketWithCustomer[]>;

  // Utility methods
  getNextTicketSequence(date: string): Promise<number>;
}

export class PgStorage implements IStorage {
  // User methods
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const [user] = await db
      .insert(users)
      .values({
        ...insertUser,
        id,
        createdAt: new Date(),
        email: insertUser.email || null,
        phone: insertUser.phone || null,
      })
      .returning();
    return user;
  }

  // Customer methods
  async getCustomer(id: string): Promise<Customer | undefined> {
    const [customer] = await db
      .select()
      .from(customers)
      .where(eq(customers.id, id));
    return customer || undefined;
  }

  async getCustomerByPhone(phone: string): Promise<Customer | undefined> {
    const [customer] = await db
      .select()
      .from(customers)
      .where(eq(customers.phone, phone));
    return customer || undefined;
  }

  async createCustomer(insertCustomer: InsertCustomer): Promise<Customer> {
    const id = randomUUID();
    const [customer] = await db
      .insert(customers)
      .values({
        ...insertCustomer,
        id,
        createdAt: new Date(),
        email: insertCustomer.email || null,
        address: insertCustomer.address || null,
      })
      .returning();
    return customer;
  }

  async getAllCustomers(): Promise<Customer[]> {
    return await db.select().from(customers).orderBy(desc(customers.createdAt));
  }

  // Ticket methods
  async getTicket(id: string): Promise<Ticket | undefined> {
    const [ticket] = await db.select().from(tickets).where(eq(tickets.id, id));
    return ticket || undefined;
  }

  async getTicketByTicketId(ticketId: string): Promise<Ticket | undefined> {
    const [ticket] = await db
      .select()
      .from(tickets)
      .where(eq(tickets.ticketId, ticketId));
    return ticket || undefined;
  }

  async createTicket(
    ticketData: InsertTicket & { ticketId: string; customerId: string }
  ): Promise<Ticket> {
    const id = randomUUID();
    const [ticket] = await db
      .insert(tickets)
      .values({
        ...ticketData,
        id,
        serviceNotes: [],
        createdAt: new Date(),
        completedAt: null,
        deviceModel: ticketData.deviceModel || null,
        serialNumber: ticketData.serialNumber || null,
        purchaseDate: ticketData.purchaseDate || null,
        estimatedCost:
          ticketData.estimatedCost !== undefined &&
          ticketData.estimatedCost !== null
            ? String(ticketData.estimatedCost)
            : null,
        finalCost:
          ticketData.finalCost !== undefined && ticketData.finalCost !== null
            ? String(ticketData.finalCost)
            : null,
        priority: ticketData.priority || "Medium",
        serviceStatus: ticketData.serviceStatus || "Pending",
        paymentStatus: ticketData.paymentStatus || "Pending",
      })
      .returning();
    return ticket;
  }

  async updateTicketStatus(
    ticketId: string,
    update: UpdateTicketStatus & { technicianId?: string }
  ): Promise<Ticket | undefined> {
    const [ticket] = await db
      .select()
      .from(tickets)
      .where(eq(tickets.ticketId, ticketId));
    if (!ticket) return undefined;

    const [updatedTicket] = await db
      .update(tickets)
      .set({
        serviceStatus: update.serviceStatus,
        priority: update.priority || ticket.priority,
        paymentStatus: update.paymentStatus || ticket.paymentStatus,
        finalCost:
          update.finalCost !== undefined && update.finalCost !== null
            ? String(update.finalCost)
            : ticket.finalCost,
        assignedTechnician: update.technicianId || ticket.assignedTechnician,
        completedAt:
          update.serviceStatus === "Completed"
            ? new Date()
            : ticket.completedAt,
      })
      .where(eq(tickets.id, ticket.id))
      .returning();

    // Add service note if provided
    if (update.serviceNote && update.technicianId) {
      const newNote = {
        id: randomUUID(),
        note: update.serviceNote,
        technicianId: update.technicianId,
        timestamp: new Date().toISOString(),
        photos: update.photos || [],
      };

      await db
        .update(tickets)
        .set({
          serviceNotes: sql`${tickets.serviceNotes} || ${JSON.stringify([
            newNote,
          ])}::jsonb`,
        })
        .where(eq(tickets.ticketId, ticketId));
    }

    return updatedTicket || undefined;
  }

  async getAllTickets(): Promise<TicketWithCustomer[]> {
    // Join tickets, customers, and users for technician name
    const rows = await db
      .select({
        id: tickets.id,
        ticketId: tickets.ticketId,
        customerId: tickets.customerId,
        deviceType: tickets.deviceType,
        deviceModel: tickets.deviceModel,
        serialNumber: tickets.serialNumber,
        purchaseDate: tickets.purchaseDate,
        issueCategory: tickets.issueCategory,
        problemDescription: tickets.problemDescription,
        priority: tickets.priority,
        serviceStatus: tickets.serviceStatus,
        paymentStatus: tickets.paymentStatus,
        estimatedCost: tickets.estimatedCost,
        finalCost: tickets.finalCost,
        assignedTechnician: tickets.assignedTechnician,
        serviceNotes: tickets.serviceNotes,
        createdAt: tickets.createdAt,
        completedAt: tickets.completedAt,
        customerName: customers.name,
        customerPhone: customers.phone,
        customerEmail: customers.email,
        customerAddress: customers.address,
        customerCreatedAt: customers.createdAt,
        assignedTechnicianName: users.fullName,
      })
      .from(tickets)
      .innerJoin(customers, eq(tickets.customerId, customers.id))
      .leftJoin(users, eq(tickets.assignedTechnician, users.id))
      .orderBy(desc(tickets.createdAt));
    return rows as TicketWithCustomer[];
  }

  async getTicketsByTechnician(
    technicianId: string
  ): Promise<TicketWithCustomer[]> {
    const rows = await db
      .select({
        ...tickets,
        customer: customers,
        assignedTechnicianName: sql`u.fullName`,
      })
      .from(tickets)
      .innerJoin(customers, eq(tickets.customerId, customers.id))
      .leftJoin(users, eq(tickets.assignedTechnician, users.id))
      .where(eq(tickets.assignedTechnician, technicianId))
      .orderBy(desc(tickets.createdAt));
    return rows as TicketWithCustomer[];
  }

  async getTicketsByStatus(status: string): Promise<TicketWithCustomer[]> {
    const rows = await db
      .select({
        ...tickets,
        customer: customers,
        assignedTechnicianName: sql`u.fullName`,
      })
      .from(tickets)
      .innerJoin(customers, eq(tickets.customerId, customers.id))
      .leftJoin(users, eq(tickets.assignedTechnician, users.id))
      .where(eq(tickets.serviceStatus, status))
      .orderBy(desc(tickets.createdAt));
    return rows as TicketWithCustomer[];
  }

  async getNextTicketSequence(date: string): Promise<number> {
    const [row] = await db
      .select()
      .from(ticket_sequences)
      .where(eq(ticket_sequences.date, date));
    let nextSequence = 1;
    if (row) {
      const currentSeq = row.sequence ? parseInt(row.sequence, 10) : 0;
      nextSequence = currentSeq + 1;

      await db
        .update(ticket_sequences)
        .set({ sequence: nextSequence.toString() }) // still string in DB
        .where(eq(ticket_sequences.date, date));
      console.log('Updated ticket sequence:', nextSequence);
    } else {
      await db
        .insert(ticket_sequences)
        .values({ date, sequence: nextSequence.toString() });
    }

    return nextSequence;
  }
}

export const storage = new PgStorage();

