import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useQuery } from "@tanstack/react-query";
import { AuthService } from "@/lib/auth";
import { getStatusColor, formatDate } from "@/lib/ticket-utils";
import { type TicketWithCustomer } from "@shared/schema";
import { MessageSquare, Smartphone, Edit } from "lucide-react";
import StatusUpdateForm from "./status-update-form";
import { useState } from "react";

interface OrderDetailsModalProps {
  isOpen: boolean;
  onClose: () => void;
  ticketId: string | null;
  onSendMessage: (ticket: TicketWithCustomer, type: 'whatsapp' | 'sms') => void;
}

export default function OrderDetailsModal({ 
  isOpen, 
  onClose, 
  ticketId, 
  onSendMessage 
}: OrderDetailsModalProps) {
  const [showStatusUpdate, setShowStatusUpdate] = useState(false);
  const { data: ticket, isLoading } = useQuery<TicketWithCustomer>({
    queryKey: ['/api/tickets', ticketId],
    enabled: isOpen && !!ticketId,
  });

  const user = AuthService.getUser();
  const canSendMessage = user?.role === 'frontdesk' && ticket?.serviceStatus === 'Completed';

  if (isLoading) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Loading...</DialogTitle>
          </DialogHeader>
          <div className="flex justify-center py-8">
            <div className="loading-spinner"></div>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  if (!ticket) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Error</DialogTitle>
          </DialogHeader>
          <p>Ticket not found.</p>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle data-testid="modal-title">
            Order Details - {ticket.ticketId}
          </DialogTitle>
        </DialogHeader>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Customer Information */}
          <div>
            <h4 className="font-medium text-foreground mb-4">Customer Information</h4>
            <div className="space-y-2">
              <p><span className="text-muted-foreground">Name:</span> <span data-testid="customer-name">{ticket.customer.name}</span></p>
              <p><span className="text-muted-foreground">Phone:</span> <span data-testid="customer-phone">{ticket.customer.phone}</span></p>
              {ticket.customer.email && (
                <p><span className="text-muted-foreground">Email:</span> <span data-testid="customer-email">{ticket.customer.email}</span></p>
              )}
              {ticket.customer.address && (
                <p><span className="text-muted-foreground">Address:</span> <span data-testid="customer-address">{ticket.customer.address}</span></p>
              )}
            </div>
          </div>

          {/* Device Information */}
          <div>
            <h4 className="font-medium text-foreground mb-4">Device Information</h4>
            <div className="space-y-2">
              <p><span className="text-muted-foreground">Type:</span> <span data-testid="device-type">{ticket.deviceType}</span></p>
              {ticket.deviceModel && (
                <p><span className="text-muted-foreground">Brand/Model:</span> <span data-testid="device-model">{ticket.deviceModel}</span></p>
              )}
              {ticket.serialNumber && (
                <p><span className="text-muted-foreground">Serial Number:</span> <span data-testid="device-serial">{ticket.serialNumber}</span></p>
              )}
              <p><span className="text-muted-foreground">Issue Category:</span> <span data-testid="issue-category">{ticket.issueCategory}</span></p>
              <p><span className="text-muted-foreground">Priority:</span> 
                <Badge className={`ml-2 ${getStatusColor(ticket.priority)}`} variant="secondary" data-testid="priority-badge">
                  {ticket.priority}
                </Badge>
              </p>
            </div>
          </div>
        </div>

        {/* Service Information */}
        <div className="mt-6">
          <h4 className="font-medium text-foreground mb-4">Service Information</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <p><span className="text-muted-foreground">Service Status:</span> 
                <Badge className={`ml-2 ${getStatusColor(ticket.serviceStatus)}`} variant="secondary" data-testid="service-status-badge">
                  {ticket.serviceStatus}
                </Badge>
              </p>
              <p><span className="text-muted-foreground">Payment Status:</span> 
                <Badge className={`ml-2 ${getStatusColor(ticket.paymentStatus)}`} variant="secondary" data-testid="payment-status-badge">
                  {ticket.paymentStatus}
                </Badge>
              </p>
              <p><span className="text-muted-foreground">Created:</span> <span data-testid="created-date">{formatDate(ticket.createdAt!)}</span></p>
              {ticket.completedAt && (
                <p><span className="text-muted-foreground">Completed:</span> <span data-testid="completed-date">{formatDate(ticket.completedAt)}</span></p>
              )}
            </div>

            <div className="space-y-2">
              {ticket.estimatedCost && (
                <p><span className="text-muted-foreground">Estimated Cost:</span> <span data-testid="estimated-cost">₹{ticket.estimatedCost}</span></p>
              )}
              {ticket.finalCost && (
                <p><span className="text-muted-foreground">Final Cost:</span> <span data-testid="final-cost">₹{ticket.finalCost}</span></p>
              )}
              {ticket.assignedTechnicianName && (
                <p><span className="text-muted-foreground">Assigned Technician:</span> <span data-testid="assigned-technician">{ticket.assignedTechnicianName}</span></p>
              )}
            </div>
          </div>
        </div>

        {/* Problem Description */}
        <div className="mt-6">
          <h4 className="font-medium text-foreground mb-2">Problem Description</h4>
          <p className="text-sm text-muted-foreground bg-muted p-3 rounded" data-testid="problem-description">
            {ticket.problemDescription}
          </p>
        </div>

        {/* Service Notes */}
        {ticket.serviceNotes && ticket.serviceNotes.length > 0 && (
          <div className="mt-6">
            <h4 className="font-medium text-foreground mb-4">Service Notes</h4>
            <div className="space-y-3">
              {ticket.serviceNotes.map((note) => (
                <div key={note.id} className="bg-muted p-3 rounded" data-testid={`service-note-${note.id}`}>
                  <p className="text-sm">{note.note}</p>
                  <p className="text-xs text-muted-foreground mt-1">
                    {new Date(note.timestamp).toLocaleString()} 
                  </p>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Status Update Form */}
        {showStatusUpdate && (
          <div className="mt-6 border-t border-border pt-6">
            <StatusUpdateForm
              ticket={ticket}
              onClose={() => setShowStatusUpdate(false)}
            />
          </div>
        )}

        {/* Actions */}
        <div className="mt-6 flex flex-wrap gap-4">
          {canSendMessage && (
            <>
              <Button
                onClick={() => onSendMessage(ticket, 'whatsapp')}
                className="bg-green-600 hover:bg-green-700"
                data-testid="button-send-whatsapp"
              >
                <MessageSquare className="mr-2 h-4 w-4" />
                Send WhatsApp
              </Button>
              <Button
                onClick={() => onSendMessage(ticket, 'sms')}
                variant="outline"
                data-testid="button-send-sms"
              >
                <Smartphone className="mr-2 h-4 w-4" />
                Send SMS
              </Button>
            </>
          )}
          <Button
            onClick={() => setShowStatusUpdate(!showStatusUpdate)}
            variant="secondary"
            data-testid="button-update-status"
          >
            <Edit className="mr-2 h-4 w-4" />
            {showStatusUpdate ? 'Hide Update' : 'Update Status'}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

frontend/client/src/components/orders-ta.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { MessageSquare, Edit, Eye } from "lucide-react";
import { getStatusColor, formatShortDate } from "@/lib/ticket-utils";
import { type TicketWithCustomer } from "@shared/schema";

interface OrdersTableProps {
  tickets: TicketWithCustomer[];
  onShowDetails: (ticketId: string) => void;
  onSendMessage: (ticket: TicketWithCustomer) => void;
  userRole: string;
  showActions?: boolean;
}


export default function OrdersTable({ 
  tickets, 
  onShowDetails, 
  onSendMessage, 
  userRole,
  showActions = true 
}: OrdersTableProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");

  const filteredTickets = tickets.filter(ticket => {
    const matchesSearch = 
      ticket.ticketId.toLowerCase().includes(searchTerm.toLowerCase()) ||
      ticket.customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      ticket.deviceType.toLowerCase().includes(searchTerm.toLowerCase()) ||
      ticket.problemDescription.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = statusFilter === "all" || ticket.serviceStatus === statusFilter;

    return matchesSearch && matchesStatus;
  });

  return (
    <Card>
      <CardHeader>
        <div className="flex justify-between items-center">
          <CardTitle>
            {userRole === 'technician' ? 'My Assigned Orders' : 'All Orders'}
          </CardTitle>
          <div className="flex space-x-4">
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-40" data-testid="select-status-filter">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="Pending">Pending</SelectItem>
                <SelectItem value="In Progress">In Progress</SelectItem>
                <SelectItem value="Completed">Completed</SelectItem>
                <SelectItem value="Delivered">Delivered</SelectItem>
              </SelectContent>
            </Select>
            <Input
              placeholder="Search orders..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-64"
              data-testid="input-search-orders"
            />
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {/* Desktop Table */}
        <div className="table-responsive overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="border-b border-border">
                <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Ticket ID</th>
                <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground w-48">Customer</th>
                <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground w-40">Device</th>
                <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground w-60">Issue</th>
                <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Service Status</th>
                <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Payment Status</th>
                <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Date</th>
                {showActions && (
                  <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Actions</th>
                )}
              </tr>
            </thead>
            <tbody>
              {filteredTickets.map((ticket) => (
                <tr key={ticket.id} className="border-b border-border hover:bg-muted/50">
                  <td className="py-3 px-4 text-sm font-medium text-primary cursor-pointer"
                      onClick={() => onShowDetails(ticket.ticketId)}
                      data-testid={`ticket-link-${ticket.ticketId}`}>
                    {ticket.ticketId}
                  </td>
                  <td className="py-3 px-4 text-sm text-foreground w-48">{ticket.customer?.name}</td>
                  <td className="py-3 px-4 text-sm text-foreground w-40">{ticket.deviceType}</td>
                  <td className="py-3 px-4 text-sm text-foreground max-w-60 truncate" title={ticket.problemDescription}>
                    {ticket.problemDescription}
                  </td>
                  <td className="py-3 px-4">
                    <Badge className={getStatusColor(ticket.serviceStatus)} variant="secondary">
                      {ticket.serviceStatus}
                    </Badge>
                  </td>
                  <td className="py-3 px-4">
                    <Badge className={getStatusColor(ticket.paymentStatus)} variant="secondary">
                      {ticket.paymentStatus}
                    </Badge>
                  </td>
                  <td className="py-3 px-4 text-sm text-muted-foreground">
                    {formatShortDate(ticket.createdAt!)}
                  </td>
                  {showActions && (
                    <td className="py-3 px-4">
                      <div className="flex space-x-2">
                        <Button 
                          size="sm" 
                          variant="ghost"
                          onClick={() => onShowDetails(ticket.ticketId)}
                          data-testid={`button-view-${ticket.ticketId}`}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                        {userRole === 'frontdesk' && ticket.serviceStatus === 'Completed' && (
                          <Button 
                            size="sm" 
                            variant="ghost"
                            onClick={() => onSendMessage(ticket)}
                            data-testid={`button-message-${ticket.ticketId}`}
                          >
                            <MessageSquare className="h-4 w-4" />
                          </Button>
                        )}
                      </div>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Mobile Cards */}
        <div className="cards-responsive space-y-4">
          {filteredTickets.map((ticket) => (
            <div key={ticket.id} className="border border-border rounded-lg p-4 hover:shadow-md transition-shadow duration-200">
              <div className="flex justify-between items-start mb-2">
                <h4 
                  className="font-medium text-primary cursor-pointer"
                  onClick={() => onShowDetails(ticket.ticketId)}
                  data-testid={`ticket-card-${ticket.ticketId}`}
                >
                  {ticket.ticketId}
                </h4>
                <div className="flex flex-col space-y-1">
                  <Badge className={getStatusColor(ticket.serviceStatus)} variant="secondary">
                    {ticket.serviceStatus}
                  </Badge>
                  <Badge className={getStatusColor(ticket.paymentStatus)} variant="secondary">
                    {ticket.paymentStatus}
                  </Badge>
                </div>
              </div>
              <p className="text-sm text-foreground mb-1">{ticket.customer?.name} - {ticket.deviceType}</p>
              <p className="text-sm text-muted-foreground mb-2 line-clamp-2">{ticket.problemDescription}</p>
              <div className="flex justify-between items-center">
                <p className="text-xs text-muted-foreground">{formatShortDate(ticket.createdAt!)}</p>
                {showActions && (
                  <div className="flex space-x-2">
                    <Button 
                      size="sm" 
                      variant="ghost"
                      onClick={() => onShowDetails(ticket.ticketId)}
                    >
                      <Eye className="h-4 w-4" />
                    </Button>
                    {userRole === 'frontdesk' && ticket.serviceStatus === 'Completed' && (
                      <Button 
                        size="sm" 
                        variant="ghost"
                        onClick={() => onSendMessage(ticket)}
                      >
                        <MessageSquare className="h-4 w-4" />
                      </Button>
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>

        {filteredTickets.length === 0 && (
          <div className="text-center py-8">
            <p className="text-muted-foreground">No orders found matching your criteria.</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

frontend/client/src/components/reports-d.content:
import { useQuery } from "@tanstack/react-query";
import { TrendingUp, TrendingDown, DollarSign, Clock, Users, Wrench, BarChart3, Activity, Download } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import * as XLSX from 'xlsx';
import { Button } from "@/components/ui/button";

interface ReportsData {
  summary: {
    totalTickets: number;
    totalCustomers: number;
    totalRevenue: number;
    avgResolutionDays: number;
  };
  revenue: {
    today: number;
    thisWeek: number;
    thisMonth: number;
  };
  statusBreakdown: {
    pending: number;
    inProgress: number;
    waitingForParts: number;
    testing: number;
    completed: number;
    delivered: number;
  };
  topIssues: Array<{ category: string; count: number }>;
  recentTickets: any[];
}

export default function ReportsDashboard() {
  const { data: reports, isLoading } = useQuery<ReportsData>({
    queryKey: ['/api/reports'],
  });
  const { data: customers = [] } = useQuery<any[]>({
    queryKey: ['/api/customers'],
  });

  const downloadCustomersExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(customers.map((customer: any) => ({
      'Customer Name': customer.name,
      'Phone': customer.phone,
      'Email': customer.email || '',
      'Address': customer.address || '',
      'Joined Date': new Date(customer.createdAt).toLocaleDateString(),
    })));
    
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Customers');
    XLSX.writeFile(workbook, `customers_${new Date().toISOString().slice(0, 10)}.xlsx`);
  };

  const downloadOrdersExcel = () => {
    if (!reports?.recentTickets) return;
    
    const worksheet = XLSX.utils.json_to_sheet(reports.recentTickets.map((ticket: any) => ({
      'Ticket ID': ticket.ticketId,
      'Customer Name': ticket.customer?.name || '',
      'Phone': ticket.customer?.phone || '',
      'Device Type': ticket.deviceType,
      'Device Model': ticket.deviceModel || '',
      'Issue Category': ticket.issueCategory,
      'Problem Description': ticket.problemDescription,
      'Status': ticket.serviceStatus,
      'Priority': ticket.priority,
      'Payment Status': ticket.paymentStatus,
      'Estimated Cost': ticket.estimatedCost || '',
      'Final Cost': ticket.finalCost || '',
      'Advance Amount': ticket.advanceAmount || '0',
      'Assigned Technician': ticket.assignedTechnicianName || '',
      'Created Date': new Date(ticket.createdAt).toLocaleDateString(),
      'Completed Date': ticket.completedAt ? new Date(ticket.completedAt).toLocaleDateString() : '',
    })));
    
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Orders');
    XLSX.writeFile(workbook, `orders_${new Date().toISOString().slice(0, 10)}.xlsx`);
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="h-32 bg-secondary animate-pulse rounded"></div>
          ))}
        </div>
        <div className="grid gap-6 md:grid-cols-2">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="h-64 bg-secondary animate-pulse rounded"></div>
          ))}
        </div>
      </div>
    );
  }

  if (!reports) {
    return (
      <Card>
        <CardContent className="pt-6 text-center text-muted-foreground">
          Failed to load reports data
        </CardContent>
      </Card>
    );
  }

  const totalStatusCount = Object.values(reports.statusBreakdown).reduce((sum, count) => sum + count, 0);

  return (
    <div className="space-y-6">
      {/* Download Section */}
      <div className="flex flex-wrap gap-4">
        <Button 
          onClick={downloadCustomersExcel}
          className="flex items-center gap-2"
          data-testid="button-download-customers"
        >
          <Download className="h-4 w-4" />
          Download Customers Excel
        </Button>
        <Button 
          onClick={downloadOrdersExcel}
          variant="outline"
          className="flex items-center gap-2"
          disabled={!reports?.recentTickets?.length}
          data-testid="button-download-orders"
        >
          <Download className="h-4 w-4" />
          Download Orders Excel
        </Button>
      </div>
      {/* Summary Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card data-testid="card-total-tickets">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Tickets</CardTitle>
            <Wrench className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-total-tickets">
              {reports.summary.totalTickets}
            </div>
            <p className="text-xs text-muted-foreground">All time tickets</p>
          </CardContent>
        </Card>

        <Card data-testid="card-total-customers">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Customers</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-total-customers">
              {reports.summary.totalCustomers}
            </div>
            <p className="text-xs text-muted-foreground">Registered customers</p>
          </CardContent>
        </Card>

        <Card data-testid="card-total-revenue">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Revenue</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-total-revenue">
              ₹{reports.summary.totalRevenue.toLocaleString()}
            </div>
            <p className="text-xs text-muted-foreground">From completed orders</p>
          </CardContent>
        </Card>

        <Card data-testid="card-avg-resolution">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Avg Resolution</CardTitle>
            <Clock className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-avg-resolution">
              {reports.summary.avgResolutionDays}
            </div>
            <p className="text-xs text-muted-foreground">Days to complete</p>
          </CardContent>
        </Card>
      </div>

      {/* Revenue Breakdown */}
      <div className="grid gap-6 md:grid-cols-2">
        <Card data-testid="card-revenue-breakdown">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="h-5 w-5" />
              Revenue Breakdown
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex justify-between items-center">
              <span className="text-sm text-muted-foreground">Today</span>
              <span className="font-medium" data-testid="text-revenue-today">₹{reports.revenue.today.toLocaleString()}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-muted-foreground">This Week</span>
              <span className="font-medium" data-testid="text-revenue-week">₹{reports.revenue.thisWeek.toLocaleString()}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-muted-foreground">This Month</span>
              <span className="font-medium" data-testid="text-revenue-month">₹{reports.revenue.thisMonth.toLocaleString()}</span>
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-service-status">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Activity className="h-5 w-5" />
              Service Status Breakdown
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {Object.entries(reports.statusBreakdown).map(([status, count]) => {
              const percentage = totalStatusCount > 0 ? (count / totalStatusCount) * 100 : 0;
              const statusLabels: Record<string, string> = {
                pending: "Pending",
                inProgress: "In Progress", 
                waitingForParts: "Waiting for Parts",
                testing: "Testing",
                completed: "Completed",
                delivered: "Delivered"
              };
              
              const statusColors: Record<string, string> = {
                pending: "bg-yellow-500",
                inProgress: "bg-blue-500",
                waitingForParts: "bg-orange-500", 
                testing: "bg-purple-500",
                completed: "bg-green-500",
                delivered: "bg-emerald-500"
              };

              return (
                <div key={status} className="space-y-1">
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-muted-foreground">{statusLabels[status]}</span>
                    <span className="font-medium" data-testid={`text-status-${status}`}>{count}</span>
                  </div>
                  <Progress value={percentage} className="h-2" />
                </div>
              );
            })}
          </CardContent>
        </Card>

        <Card data-testid="card-top-issues">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <BarChart3 className="h-5 w-5" />
              Top Issues
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {reports.topIssues.map((issue, index) => (
                <div key={issue.category} className="flex justify-between items-center">
                  <div className="flex items-center gap-2">
                    <Badge variant="outline" className="text-xs">{index + 1}</Badge>
                    <span className="text-sm" data-testid={`text-issue-${index}`}>{issue.category}</span>
                  </div>
                  <span className="font-medium text-sm" data-testid={`text-issue-count-${index}`}>
                    {issue.count} tickets
                  </span>
                </div>
              ))}
              {reports.topIssues.length === 0 && (
                <p className="text-sm text-muted-foreground text-center">No issues reported yet</p>
              )}
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-recent-activity">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Clock className="h-5 w-5" />
              Recent Activity
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {reports.recentTickets.slice(0, 5).map((ticket) => (
                <div key={ticket.id} className="flex justify-between items-center border-b border-border last:border-0 pb-2 last:pb-0">
                  <div>
                    <p className="text-sm font-medium" data-testid={`text-ticket-${ticket.id}`}>
                      {ticket.ticketId}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      {ticket.customer?.name} - {ticket.deviceType}
                    </p>
                  </div>
                  <Badge 
                    variant={ticket.serviceStatus === 'Completed' ? 'default' : 'secondary'}
                    data-testid={`badge-status-${ticket.id}`}
                  >
                    {ticket.serviceStatus}
                  </Badge>
                </div>
              ))}
              {reports.recentTickets.length === 0 && (
                <p className="text-sm text-muted-foreground text-center">No recent activity</p>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

frontend/client/src/components/sidebar.t.content:
import { Link, useLocation } from "wouter";
import { Home, Plus, List, CheckCircle, Users, BarChart3, Wrench, FileText, LogOut } from "lucide-react";
import { Button } from "@/components/ui/button";
import { AuthService } from "@/lib/auth";

interface SidebarProps {
  userRole: string;
  isOpen: boolean;
  onToggle: () => void;
}

export default function Sidebar({ userRole, isOpen, onToggle }: SidebarProps) {
  const [location] = useLocation();

  const frontdeskNavItems = [
    { href: "/dashboard", label: "Dashboard", icon: Home },
    { href: "/add-ticket", label: "Add Application", icon: Plus },
    { href: "/orders", label: "All Orders", icon: List },
    { href: "/completed-orders", label: "Completed Orders", icon: CheckCircle },
    { href: "/customers", label: "Customers", icon: Users },
    { href: "/reports", label: "Reports", icon: BarChart3 },
  ];

  const technicianNavItems = [
    { href: "/tech-dashboard", label: "Dashboard", icon: Home },
    { href: "/assigned-orders", label: "Assigned Orders", icon: Wrench },
    { href: "/service-notes", label: "Service Notes", icon: FileText },
  ];

  const navItems = userRole === 'frontdesk' ? frontdeskNavItems : technicianNavItems;

  const handleLinkClick = () => {
    if (window.innerWidth < 1024) {
      onToggle();
    }
  };

  return (
    <>
      {/* Mobile overlay */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={onToggle}
        />
      )}

      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-sidebar transform transition-transform duration-200 ease-in-out lg:translate-x-0 ${
        isOpen ? 'translate-x-0' : '-translate-x-full'
      }`}>
        <div className="flex items-center justify-center h-16 bg-sidebar-primary">
          <h1 className="text-xl font-bold text-sidebar-primary-foreground">Satyasri</h1>
        </div>

        <nav className="mt-8 px-4">
          <div className="space-y-2">
            {navItems.map((item) => {
              const Icon = item.icon;
              const isActive = location === item.href;

              return (
                <Link key={item.href} href={item.href}>
                  <Button
                    variant="ghost"
                    className={`w-full justify-start text-sidebar-foreground hover:bg-sidebar-accent hover:text-sidebar-accent-foreground ${
                      isActive ? 'bg-sidebar-accent text-sidebar-accent-foreground' : ''
                    }`}
                    onClick={handleLinkClick}
                    data-testid={`nav-${item.href.slice(1)}`}
                  >
                    <Icon className="mr-3 h-4 w-4" />
                    {item.label}
                  </Button>
                </Link>
              );
            })}
          </div>

          <div className="mt-8 pt-8 border-t border-sidebar-border">
            <Button
              variant="ghost"
              className="w-full justify-start text-sidebar-foreground hover:bg-sidebar-accent hover:text-sidebar-accent-foreground"
              onClick={AuthService.logout}
              data-testid="button-logout"
            >
              <LogOut className="mr-3 h-4 w-4" />
              Logout
            </Button>
          </div>
        </nav>
      </div>
    </>
  );
}

